             jq('#nhif').change(onNHIFStatusChange);
             jq('#wcirc').change(onHipOrWaistCircChange);  
             jq('#hip_circ').change(onHipOrWaistCircChange); 
            getField('weight.value').change(onWeightOrHeightChange);
            getField('height.value').change(onWeightOrHeightChange);            
            onWeightOrHeightChange();
            displayRecentBmi();            
             displayRecentDiabeticStatus();
             displayRecentHTNStatus();
             disableGDMFormMale();

                         // medication flags
            var categories = medCategory();
            listenTOItemChanges(categories.notAllowed[0].listA,flagNotAllowed);
            listenTOItemChanges(categories.notAllowed[0].listB,flagNotAllowed);            

            listenTOItemChanges(categories.allowedNotFrequntyUsed[0].BetaBlockers,flagAllowedNotFrequently);
            listenTOItemChanges(categories.allowedNotFrequntyUsed[0].ARB,flagAllowedNotFrequently);
            listenTOItemChanges(categories.allowedNotFrequntyUsed[1].ACEInhibitors,flagAllowedNotFrequently);

            listenTOItemChanges(categories.allowed[0].BetaBlockers,flagAllowedCombination);
            listenTOItemChanges(categories.allowed[0].ThiazideDiuretic,flagAllowedCombination);
            listenTOItemChanges(categories.allowed[1].ARB,flagAllowedCombination);
            listenTOItemChanges(categories.allowed[2].ACEInhibitors,flagAllowedCombination);


            var tabsObj=jq( "#tabs" ).tabs();
            jq(".ui-tabs-panel").each(function(i){

			  var totalSize = jq(".ui-tabs-panel").size() - 1;
	         var prevTag='';
             var nextTag='';
			  if (i != totalSize) {
			      next = i + 2;
                  nextTag='
Next Page »';

                  if(next == 2)
                  nextTag='
Next Page »';
			  }

			  if (i != 0) {
			      prev = i;
                  prevTag='
« Prev Page';
			  }



              if(prevTag){
                if(nextTag) jq(this).append('
' + prevTag + nextTag+'');
              }

              if(prevTag){
                if(!nextTag) jq(this).append('
' + prevTag+'');
              }

              if(nextTag){
                if(!prevTag) jq(this).append('
' + nextTag +'');
              }


			});

			jq('.next, .previous').click(function() { 
		           tabsObj.tabs('select', jq(this).attr("rel"));
