htmlform.script:

        function onWeightOrHeightChange() {
            var display = '';
            var weightKg = parseFloat(getValue('weight.value'));
            var heightM = parseFloat(getValue('height.value')) / 100;
            var displayDiv = 'calculated-bmi';
            if (!isNaN(weightKg) && !isNaN(heightM))  {
                var bmi = weightKg / (heightM * heightM);
                if (isNaN(bmi) || bmi < 10 || bmi > 50) {
                    display = 'Abnormal value. Were weight and height entered correctly?';
                }
                else {
                    display = bmi.toFixed(2);
                    flagBMI(display,displayDiv);
                }
            }

            jq('#calculated-bmi').html(display);
        }

        function flagBMI(bmi,displayDiv) {
                    var display =displayDiv;
                    var normal_range_min = 18.50;
                    var normal_range_max = 24.99;
                    var overweight = 25.00;
                    var obese = 30.00; 
                    jq('#'+display).last().removeClass("flag_green");
                    jq('#'+display).last().removeClass("flag_yellow");
                    jq('#'+display).last().removeClass("flag_red");

        if(bmi == obese || bmi > obese){
            jq('#'+display).last().addClass("flag_red");
            return;
        }  

        if(bmi == overweight || bmi >overweight){
            jq('#'+display).last().addClass("flag_yellow");
            return;
        }

        if(bmi == normal_range_min || bmi >normal_range_min){
            jq('#'+display).last().addClass("flag_green");
            return;
        }

        if(bmi < normal_range_min){
            jq('#'+display).last().addClass("flag_red");
            return;
        }
        }
        function displayRecentBmi(){
            var weightKg = ""
            var heightM = ("")/100
            var bmi ='';
            var displayDiv = 'recent-calculated-bmi';
            if (!isNaN(weightKg) && !isNaN(heightM))  {
                bmi = weightKg / (heightM * heightM);
                flagBMI(bmi,displayDiv);
            }

            jq('#recent-calculated-bmi').html(bmi.toFixed(2));
        }

        function displayRecentDiabeticStatus(){
            var lastObs = "";            

            var sectionId = 'diabetes_status';            

            if(lastObs){
             lastObs = lastObs.trim().split('#')[1];
             selectElementByValue(sectionId,lastObs);
            }

        }        
        function displayRecentHTNStatus(){
            var lastObs = ""           
            var sectionId = 'htn_status';            
            if(lastObs){
             lastObs = lastObs.trim().split('#')[1];
             selectElementByValue(sectionId,lastObs);
            }    
        }
        function disableGDMFormMale(){
             var gender= "";
             var sectionId = 'type_of_diabetes';

            if(gender=='M'){
            disableElementByValue(sectionId,1449);
            jq('#pdt :input').prop('disabled', true);            
            }

        }
        function selectElementByValue(sectionId,value){
            jq('#' + sectionId + ' :input').each(function(){
            var inputElement = jq(this);
            if(inputElement.val() == value){    
                inputElement.prop("checked", true);
            }
         })
        }
        function disableElementByValue(sectionId,value){
            jq('#' + sectionId + ' :input').each(function(){
            var inputElement = jq(this);
            if(inputElement.val() == value){    
                inputElement.prop("disabled", true);
            }
         })
        }
        function diabeteStatus(){
            var val= getValue('diabetes_status.value');

            jq('#dm_diagnosis_date :input').prop('disabled', true);
            jq('#dm_treatment_start_date :input').prop('disabled', true);
            if(val == 165088){
                jq('#dm_diagnosis_date :input').prop('disabled', false);
                jq('#dm_treatment_start_date :input').prop('disabled', false);
             }
        }
        function htnStatus(){
            var val= getValue('htn_status.value');
            jq('#htn_diagnosis_date :input').prop('disabled', true);
            jq('#htn_treatment_start_date :input').prop('disabled', true);
            if(val == 165093){
                jq('#htn_diagnosis_date :input').prop('disabled', false);
                jq('#htn_treatment_start_date :input').prop('disabled', false);
             }
        }
        function onNHIFStatusChange() {
            var display = '';
            var nhif = getValue('nhif.value');            
            if (nhif==1066)  {                
                    display = 'Encourage patient to enroll';
            }            
            jq('#nhif-comment').html(display);
          }

        function hivStatus(){
            var val= getValue('hiv_status.value');
            jq('#enrolled_to_hiv_care :input').prop('disabled', true);
            if(val == 138571){
                jq('#enrolled_to_hiv_care :input').prop('disabled', false);
             }
        }
        function bPStatus(diastolic,systolic,min_systolic,max_systolic,min_diastolic,max_diastolic){
            var normal_diastolic_min = min_diastolic;
            var normal_diastolic_max = max_diastolic;

            var normal_systolic_min = min_systolic;
            var normal_systolic_max = max_systolic;

            if(systolic < normal_systolic_max || systolic == normal_systolic_max){

                if(systolic > normal_systolic_min || systolic == normal_systolic_min){

                } else {
                return false;
                }

            }else{
                return false;
            }  

            if(diastolic < normal_diastolic_max || diastolic == normal_diastolic_max){
                if(diastolic > normal_diastolic_min || diastolic == normal_diastolic_min){
                status=true;
                } else {
                return false;
                }

            }else{
                return false;
            }

            return true;
        }

        function checkBpReading(){
            var systolic = getValue('systolic.value');
            var diastolic = getValue('diastolic.value');
            removeFlags('bp_reading')
            bpFlag(diastolic,systolic,'bp_reading');
        }

        function removeFlags(display){

                    jq('#' + display).last().removeClass("green_flag");
                    jq('#' + display).last().removeClass("red_flag");
                    jq('#' + display).last().removeClass("yellow_flag");
        }
        function bpFlag(diastolic,systolic,display){
            var isNormal = false;
            var isNormalHigh = false;
            var isMild = false;
            var isModerate = false;
            var isSevere = false;
            var cBp = bpCategory();
            var isDiabetic = getValue('diabetes_status.value'); 

            isNormal = bPStatus(diastolic,systolic,
                cBp.normal.min.systolic,cBp.normal.max.systolic,
                cBp.normal.min.diastolic,cBp.normal.max.diastolic
            );

            if(isNormal){            
            if(isDiabetic == 165088){
                jq('#'+display).last().addClass("yellow_flag");             

            }else{
            jq('#' + display ).last().addClass("green_flag");
            }
            return;
            }

            isNormalHigh = bPStatus(diastolic,systolic,
                cBp.high_normal.min.systolic,cBp.high_normal.max.systolic,
                cBp.high_normal.min.diastolic,cBp.high_normal.max.diastolic
            );

            if(isNormalHigh){


            if(isDiabetic == 165088){
                jq('#' + display ).last().addClass("red_flag");
            }else{
            jq('#'+display).last().addClass("green_flag");
            }
            return;
            }

            isMild = bPStatus(diastolic,systolic,
                cBp.mild.min.systolic,cBp.mild.max.systolic,
                cBp.mild.min.diastolic,cBp.mild.max.diastolic
            );

            if(isMild){

            if(isDiabetic == 165088){
                jq('#'+display).last().addClass("red_flag");
            }else{
            jq('#' + display).last().addClass("green_flag");
            }

            return;
            }

            isModerate = bPStatus(diastolic,systolic,
                cBp.moderate.min.systolic,cBp.moderate.max.systolic,
                cBp.moderate.min.diastolic,cBp.moderate.max.diastolic
            );

            if(isModerate){
            if(isDiabetic == 165088){
                jq('#'+display).last().addClass("red_flag");
            }else{
            jq('#'+display).last().addClass("yellow_flag");
            }
            return;
            }

            isSevere = bPStatus(diastolic,systolic,
                cBp.severe.min.systolic,cBp.severe.max.systolic,
                cBp.severe.min.diastolic,cBp.severe.max.diastolic
            );

            if(isSevere){
            jq('#'+display).last().addClass("red_flag");
            return;
            }
        }

        function bpCategory(){
            return {
            normal:{ 
                min:{diastolic:80,systolic:120},
                max:{diastolic:84,systolic:129}
            },
            high_normal:{ 
                min:{diastolic:85,systolic:130},
                max:{diastolic:89,systolic:139}
            },
            mild:{ 
                min:{diastolic:90,systolic:140},
                max:{diastolic:99,systolic:159}
            },
            moderate:{ 
                min:{diastolic:100,systolic:160},
                max:{diastolic:109,systolic:179}
            },
            severe:{ 
                min:{diastolic:110,systolic:180},
                max:{diastolic:1000,systolic:1000}
            }
            };
        }

        function onHipOrWaistCircChange() {
            var display = '';
            var hip_circ = parseFloat(getValue('hip_circ.value'));
            var wcirc = parseFloat(getValue('wcirc.value'));
            if (!isNaN(wcirc) && !isNaN(hip_circ))  {
                var hipWaist = hip_circ / wcirc;
                if (isNaN(hipWaist)) {
                    display = '';
                }
                else {
                    display = hipWaist.toFixed(2);
                }
            }            
            jq('#hip_waist_ratio').html(display);
        }
        jQuery(function() {
             jq('#diastolic').change(checkBpReading);
             jq('#systolic').change(checkBpReading);
             jq('#diabetes_status').change(diabeteStatus);
             jq('#hiv_status').change(hivStatus);
             jq('#htn_status').change(htnStatus);
             jq('#nhif').change(onNHIFStatusChange);
             jq('#wcirc').change(onHipOrWaistCircChange);  
             jq('#hip_circ').change(onHipOrWaistCircChange); 
            getField('weight.value').change(onWeightOrHeightChange);
            getField('height.value').change(onWeightOrHeightChange);            
            onWeightOrHeightChange();
            displayRecentBmi();            
             displayRecentDiabeticStatus();
             displayRecentHTNStatus();
             disableGDMFormMale();


            var tabsObj=jq( "#tabs" ).tabs();
            jq(".ui-tabs-panel").each(function(i){

			  var totalSize = jq(".ui-tabs-panel").size() - 1;
	         var prevTag='';
             var nextTag='';
			  if (i != totalSize) {
			      next = i + 2;
                  nextTag='
Next Page »';

                  if(next == 2)
                  nextTag='
Next Page »';
			  }

			  if (i != 0) {
			      prev = i;
                  prevTag='
« Prev Page';
			  }



              if(prevTag){
                if(nextTag) jq(this).append('
' + prevTag + nextTag+'');
              }

              if(prevTag){
                if(!nextTag) jq(this).append('
' + prevTag+'');
              }

              if(nextTag){
                if(!prevTag) jq(this).append('
' + nextTag +'');
              }


			});

			jq('.next, .previous').click(function() { 
		           tabsObj.tabs('select', jq(this).attr("rel"));
		           return false;
		       });

        });
