        function onTemperatureChange(){     
         var display = '';
            var temp = getValue('temperature.value');                
            if (temp < 36.1 || temp > 37.1)  {            
                    display = 'Temperature should be between 36.1-37.1';
                     jq('#temperature').last().addClass("red_flag");
            }else{
                 jq('#temperature').last().removeClass("red_flag");
            }       

        }

        function flagInputByRange(inputElementId,min,max){ 
            var value = getValue(inputElementId + '.value');                    
            if (value < min || value > max)  {            
                     jq('#'+inputElementId).last().addClass("red_flag");
            }else{
                 jq('#'+inputElementId).last().removeClass("red_flag");
            }      

        }

       function flagInputByLimit(inputElementId,contraintValue,type){
        var value = getValue(inputElementId + '.value'); 
           if(type == 'min'){
                if ( value < contraintValue )  {            
                    jq('#'+inputElementId).last().addClass("red_flag");
                }else{
                   jq('#'+inputElementId).last().removeClass("red_flag");
                }   
           }  

           if(type == 'max'){
                if ( value > contraintValue)  {            
                    jq('#'+inputElementId).last().addClass("red_flag");
                }else{
                   jq('#'+inputElementId).last().removeClass("red_flag");
                }   
           }                                   

        }
