        function bPStatus(diastolic,systolic,min_systolic,max_systolic,min_diastolic,max_diastolic){
            var normal_diastolic_min = min_diastolic;
            var normal_diastolic_max = max_diastolic;

            var normal_systolic_min = min_systolic;
            var normal_systolic_max = max_systolic;

            if(systolic < normal_systolic_max || systolic == normal_systolic_max){

                if(systolic > normal_systolic_min || systolic == normal_systolic_min){

                } else {
                return false;
                }

            }else{
                return false;
            }  

            if(diastolic < normal_diastolic_max || diastolic == normal_diastolic_max){
              if(diastolic > normal_diastolic_min || diastolic == normal_diastolic_min){
                status=true;
                } else {
                return false;
                }

            }else{
                return false;
            }

            return true;
        }

        function checkFirstBpReading(){
            var systolic = getValue('systolic1.value');
            var diastolic = getValue('diastolic1.value');
            removeFlags('first_reading_bp')
            if(systolic >0 && diastolic >0  ){
            bpFlag(diastolic,systolic,'first_reading_bp');
            }
        }


        function checkSecondBpReading(){
            removeFlags('second_reading_bp')
            var systolic = getValue('systolic2.value');
            var diastolic = getValue('diastolic2.value');
            if(systolic >0 && diastolic >0  ){
               bpFlag(diastolic,systolic,'second_reading_bp');
            }
        }

        function removeFlags(display){

                    jq('#' + display).last().removeClass("green_flag");
                    jq('#' + display).last().removeClass("red_flag");
                    jq('#' + display).last().removeClass("yellow_flag");

        }
        function bpFlag(diastolic,systolic,display){
            var isNormal = false;
            var isNormalHigh = false;
            var isMild = false;
            var isModerate = false;
            var isSevere = false;
            var cBp = bpCategory();
            var isDiabetic = getValue('diabetes_status.value');        
        // Hypotension
            if(systolic < cBp.normal.min.systolic || diastolic <cBp.normal.min.diastolic){            
            if(isDiabetic == 165088 || isDiabetic == 165087){
                jq('#'+display).last().addClass("red_flag");             

            }else{
            jq('#' + display ).last().addClass("yellow_flag");
            }
            return;
            }

            isNormal = bPStatus(diastolic,systolic,
                cBp.normal.min.systolic,cBp.normal.max.systolic,
                cBp.normal.min.diastolic,cBp.normal.max.diastolic
            );

            if(isNormal){            
            if(isDiabetic == 165088 || isDiabetic == 165087){
                jq('#'+display).last().addClass("yellow_flag");             

            }else{
            jq('#' + display ).last().addClass("green_flag");
            }
            return;
            }

            isNormalHigh = bPStatus(diastolic,systolic,
                cBp.high_normal.min.systolic,cBp.high_normal.max.systolic,
                cBp.high_normal.min.diastolic,cBp.high_normal.max.diastolic
            );

            if(isNormalHigh){


            if(isDiabetic == 165088 || isDiabetic == 165087){
                jq('#' + display ).last().addClass("red_flag");
            }else{
            jq('#'+display).last().addClass("green_flag");
            }
            return;
            }

            isMild = bPStatus(diastolic,systolic,
                cBp.mild.min.systolic,cBp.mild.max.systolic,
                cBp.mild.min.diastolic,cBp.mild.max.diastolic
            );

            if(isMild){

            if(isDiabetic == 165088 || isDiabetic == 165087){
                jq('#'+display).last().addClass("red_flag");
            }else{
            jq('#' + display).last().addClass("yellow_flag");
            }

            return;
            }

            isModerate = bPStatus(diastolic,systolic,
                cBp.moderate.min.systolic,cBp.moderate.max.systolic,
                cBp.moderate.min.diastolic,cBp.moderate.max.diastolic
            );

            if(isModerate){
            if(isDiabetic == 165088 || isDiabetic == 165087){
                jq('#'+display).last().addClass("red_flag");
            }else{
            jq('#'+display).last().addClass("yellow_flag");
            }
            return;
            }

            isSevere = bPStatus(diastolic,systolic,
                cBp.severe.min.systolic,cBp.severe.max.systolic,
                cBp.severe.min.diastolic,cBp.severe.max.diastolic
            );

            if(isSevere){
            jq('#'+display).last().addClass("red_flag");
            return;
            }
        }

        function bpCategory(){
            return {
            normal:{ 
                min:{diastolic:60,systolic:90},
                max:{diastolic:84,systolic:129}
            },
            high_normal:{ 
                min:{diastolic:85,systolic:130},
                max:{diastolic:89,systolic:139}
            },
            mild:{ 
                min:{diastolic:90,systolic:140},
                max:{diastolic:99,systolic:159}
            },
            moderate:{ 
                min:{diastolic:100,systolic:160},
                max:{diastolic:109,systolic:179}
            },
            severe:{ 
                min:{diastolic:110,systolic:180},
                max:{diastolic:1000,systolic:1000}
            }
            };
        }

