		function flagMedicationSection(inputElements,cssClass,action){
        var inputElementsArray = inputElements.split(',');
        jq.each(inputElementsArray, function( index, value ) {

                if(action=='add'){

                   if( getValue(value+'.value')){
                      jq('#'+value).addClass(cssClass);
                   }

                      console.log( index + ": " + value +'cssClass'+cssClass+'=='+action);
                } 

                if(action=='remove'){
                    jq('#'+value).removeClass(cssClass);
                       console.log( index + ": " + value +'cssClass'+cssClass+'=='+action);
               }
         });

        }

        function processMedicationFlag(){
        var categories = medCategory();
        // flag not allowedr
        var hasPrimaryDrugs = hasDrugs(categories.notAllowed[0].listA);
        if(hasPrimaryDrugs){
            if(hasDrugs(categories.notAllowed[0].listB)){
              flagMedicationSection(categories.notAllowed[0].listB+categories.notAllowed[0].listA,categories.notAllowed[0].class,'add');
            }else{
              flagMedicationSection(categories.notAllowed[0].listB+categories.notAllowed[0].listA,categories.notAllowed[0].class,'remove');
            }
        }else{
          flagMedicationSection(categories.notAllowed[0].listB+categories.notAllowed[0].listA,categories.notAllowed[0].class,'remove');
        }

        }

       function flagNotAllowed(){
            var categories = medCategory();
            flagDrugCombination(categories.notAllowed[0].listA,categories.notAllowed[0].listB,categories.notAllowed[0].class);
             checkInvalidMedication();
        }

       function flagAllowedNotFrequently(){
            var categories = medCategory();
            console.log('categories.allowedNotFrequntyUsed[0].BetaBlockers' ,categories.allowedNotFrequntyUsed[0].BetaBlockers)     

            flagDrugCombination(
                    categories.allowedNotFrequntyUsed[0].BetaBlockers,
                    categories.allowedNotFrequntyUsed[0].ARB,
                    categories.allowedNotFrequntyUsed[0].class);

            flagDrugCombination(
                    categories.allowedNotFrequntyUsed[1].BetaBlockers,
                    categories.allowedNotFrequntyUsed[1].ACEInhibitors,
                    categories.allowedNotFrequntyUsed[1].class);
       }

        function flagAllowedCombination(){
            var categories = medCategory();

            flagDrugCombination(
                    categories.allowed[0].BetaBlockers,
                    categories.allowed[0].ThiazideDiuretic,
                    categories.allowed[0].class);

            flagDrugCombination(
                    categories.allowed[1].ARB,
                    categories.allowed[1].ThiazideDiuretic,
                    categories.allowed[1].class);

            flagDrugCombination(
                    categories.allowed[2].ACEInhibitors,
                    categories.allowed[2].ThiazideDiuretic,
                    categories.allowed[2].class);
       }

       function flagDrugCombination(group1,group2,cssClass){

        var hasPrimaryDrugs = hasDrugs(group1);
        if(hasPrimaryDrugs){
            if(hasDrugs(group2)){
              flagMedicationSection(group2+group1,cssClass,'add');
            }else{
              flagMedicationSection(group2+group1,cssClass,'remove');
            }
        }else{
          flagMedicationSection(group2+group1,cssClass,'remove');
        }

        }        

        function hasDrugs(obsIds){
        var drugs = obsIds.split(',');
        var hasPrimaryDrug = false;
          jq.each(drugs, function( index, value ) {
                if( getValue(value+'.value')){
                hasPrimaryDrug = true;
                return false;
            }
         });
        return hasPrimaryDrug;
        }

        function medCategory(){
            var med = medGroup();
            return {
            notAllowed:[{ 
                listA:med.ACEInhibitors,
                listB: med.ARB,
                class:'red_flag'
            }],
            allowedNotFrequntyUsed:[
            { 
                listA:med.BetaBlockers,
                listB: med.ARB,
                class:'yellow_flag'
            },
            { 
                listA:med.BetaBlockers,
                listB: med.ACEInhibitors,
                class:'yellow_flag'
            }
             ],
            allowed:[
                { 
                listA:med.BetaBlockers,
                listB:med.ThiazideDiuretic,
                class:'green_flag'
                },
                { 
                    listA:med.ARB,
                    listB:med.ThiazideDiuretic,
                    class:'green_flag'
                },
                { 
                    listA:med.ACEInhibitors,
                    listB:med.ThiazideDiuretic,
                    class:'green_flag'
                },
                { 
                    listA:med.ACEInhibitors,
                    listB:med.ARB,
                    class:'green_flag'
                }
        ]
           }
        }

        function medGroup(){
            return {
                ACEInhibitors: 'Captopril,Enalapril,Lisinopril,Perindopril,Ramipril',
                ARB: 'Candesartan,Irbesartan,Losartan,Telmisartan,Valsartan,Olmesartan',
                BetaBlockers:'Atenolol,Labetolol,Propranolol,Carvedilol,Nebivolol,Metoprolol,Bisoprolol',
                GroupC:'Amlodipine,Felodipine,Nifedipine',
                ThiazideDiuretic:'Chlorthalidone,HydrochlorothiazideHCTZ',
                ThiazideLikeDiuretic:'Indapamide',
                GroupZ:'Methyldopa,Hydralazine,Prazocin'
           }
        }

        function listenTOItemChanges(items,onchangeMethod){
