            if(items){
                jq(init + items.replace(/,/g,',#')).change(onchangeMethod);
            }            
        }
        function  showRequiredFields(){
            var requiredFields=getRequiredFields();
            var divHtml="";

              jq.each(requiredFields, function( index, fieldObject ) {
                 var fieldValue = getValue(fieldObject.name+'.value');
                  jq('#'+fieldObject.name+'_msg').html('');
                  jq('#'+fieldObject.name+'_msg').removeClass('required');
                  if(!fieldValue){
                  divHtml=divHtml+'' + fieldObject.message+'
';
                  // handle nutrition and physical exercise
                    if(fieldObject.name=='nutrition_opt' || fieldObject.name=='physical_activity_opt' ){
                        jq('#'+fieldObject.name+'_msg').html(fieldObject.message + '
');
                    }else{
                        jq('#'+fieldObject.name+'_msg').html('Required');

                    }
                    jq('#'+fieldObject.name+'_msg').addClass('required');    

                  }

                });
                jq('#displayRequiredFields').html(divHtml);
                if(divHtml){
                return true;
                }
                return false;

        }        

        function addRequiredChecksOnChange(){
            var requiredFields=getRequiredFields();
            jq.each(requiredFields, function( index, fieldObject ) {                           
                jq('#'+fieldObject.name).change(showRequiredFields);
            });

        }

