                {name:'hypertension_runs_in_family',message:'Hypertension runs in the family question is required', location:'page1'}, 
                {name:'hiv_status',message:'HIV Status is required', location:'page1'}, 
                {name:'nhif',message:'Nhif status is required', location:'page1'}, 
