        function flagWaistRatio(waistRatio) {

            var gender= "";
            var maxMaleRatio = 0.9;
            var maxFemaleRatio = 0.85;      

            jq('#hip_waist_ratio').last().removeClass("flagRed");

            if(gender=='M' && waistRatio > maxMaleRatio){
            jq('#hip_waist_ratio').last().addClass("flagRed");
            return;        
            }

            if(gender=='F' && waistRatio > maxFemaleRatio){
            jq('#hip_waist_ratio').last().addClass("flagRed");
            return;        
            }
        }

        function containsCombination(group1,group2){
        var hasPrimaryDrugs = hasDrugs(group1);
        var hasSecondaryDrugs =hasDrugs(group2);

        if(hasSecondaryDrugs && hasPrimaryDrugs) {
        return true;
        }
        return false;
       }

