        function checkInvalidMedication(){
        var categories = medCategory(); 

            var hasBoth = containsCombination(categories.notAllowed[0].listA,categories.notAllowed[0].listB);
            if (hasBoth === true) {
               jq('#displayRequiredFields').html('You have selected invalid drug combination');
               jq('#displayRequiredFields').addClass('required');
             }else {
                jq('#displayRequiredFields').removeClass('required');
                jq('#displayRequiredFields').html('');
             }
             return hasBoth;

        } 

        function initializeProviderName(){

            var userLoc;
            var server = getServerPath();   
            if(server){
                url_content('/'+server + "/ws/rest/v1/appui/session").success(function(data){ 
                userLoc = data?data.currentProvider.person:null;  
                setValue('clinician_name.value',userLoc.display);     
                });
                return userLoc;
            }

        }

        function url_content(url){
            return jq.get(url);
        }

        function getServerPath(){
        var pathname = window.location.pathname;
        return pathname?pathname.split('/')[1]:null; 
        }

        beforeSubmit.push(function() {
            // check unfilled required fields and prevent submission

            var val = showRequiredFields();
            if (val === true) {
                addRequiredChecksOnChange();
                return false;
            }

            // check medication combination and prevent submission
             var invalidMeds = checkInvalidMedication();
             if(invalidMeds === true){
               return false;
             }           

            return true;
        });
