htmlform.script:
        function onWeightOrHeightChange() {
            var display = '';
            var weightKg = parseFloat(getValue('weight.value'));
            var heightM = parseFloat(getValue('height.value')) / 100;
            if (!isNaN(weightKg) && !isNaN(heightM))  {
                var bmi = weightKg / (heightM * heightM);
                if (isNaN(bmi) || bmi < 10 || bmi > 50) {
                    display = 'Abnormal value. Were height and height entered correctly?';
                }
                else {
                    display = bmi.toFixed(2);
                    flagBMI(display)
                }
            }

            jq('#calculated-bmi').html(display);
        }

        function flagBMI(bmi) {
                    var display ='calculated-bmi';
                    var normal_range_min = 18.50;
                    var normal_range_max = 24.99;
                    var overweight = 25.00;
                    var obese = 30.00; 
                    jq('#'+display).last().removeClass("flag_green");
                    jq('#'+display).last().removeClass("flag_yellow");
                    jq('#'+display).last().removeClass("flag_red");

        if(bmi == obese || bmi > obese){
            jq('#'+display).last().addClass("flag_red");
            return;
        }  

        if(bmi == overweight || bmi >overweight){
            jq('#'+display).last().addClass("flag_yellow");
            return;
        }

        if(bmi == normal_range_min || bmi >normal_range_min){
            jq('#'+display).last().addClass("flag_green");
            return;
        }

        if(bmi < normal_range_min){
            jq('#'+display).last().addClass("flag_red");
            return;
        }
        }

        function flagLfMonofilament(){

            var lf_monofilament= getValue('lf_monofilament.value');
            if(lf_monofilament > 5){
                jq('#lf_flag').last().addClass("red_flag");
            }else{
                jq('#lf_flag').last().removeClass("red_flag");
            }
        }

        function flagRfMonofilament(){

            var rf_monofilament= getValue('rf_monofilament.value');
            jq('#rf_flag').last().removeClass("red_flag"); 
            if(rf_monofilament > 5){
                jq('#rf_flag').last().addClass("red_flag");
            }
        }
        function counsellingEducation(){
             var val= getValue('education.value');
            jq('#education_other :input').prop('disabled', true);
            if(val == 5622){
                jq('#education_other :input').prop('disabled', false);
             }
        }

        function onTemperatureChange(){      

         var display = '';
            var temp = getValue('temperature.value');                
            if (temp < 36.1 || temp > 37.1)  {            
                    display = 'Temperature should be between 36.1-37.1';
            }        

        }

        function prByAgeChange(){      
         var display = '';
            var age_6 = 72;
            var age_15 = 180;
            var age = ""  
            var pr = getValue('pr_in.value');                
            if (pr < 60 || pr > 100)  {  

                if (age < age_15 ||age == age_15|| age > age_6 || age == age_6)  {     
                    display = 'For patients aged 6-15 years should be between 60-100';
                }
            }        

        }

        function bPStatus(diastolic,systolic,min_systolic,max_systolic,min_diastolic,max_diastolic){
                    var normal_diastolic_min = min_diastolic;
                    var normal_diastolic_max = max_diastolic;

                    var normal_systolic_min = min_systolic;
                    var normal_systolic_max = max_systolic;

                    if(systolic < normal_systolic_max || systolic == normal_systolic_max){

                        if(systolic > normal_systolic_min || systolic == normal_systolic_min){

                        } else {
                        return false;
                        }

                    }else{
                        return false;
                    }  

                    if(diastolic < normal_diastolic_max || diastolic == normal_diastolic_max){
                        if(diastolic > normal_diastolic_min || diastolic == normal_diastolic_min){
                        status=true;
                        } else {
                        return false;
                        }

                    }else{
                        return false;
                    }

                return true;
        }

    function checkFirstBpReading(){
        var systolic = getValue('systolic1.value');
        var diastolic = getValue('diastolic1.value');
        removeFlags('first_reading_bp')
        bpFlag(diastolic,systolic,'first_reading_bp');
    }

    function checkSecondBpReading(){
        removeFlags('second_reading_bp')
        var systolic = getValue('systolic2.value');
        var diastolic = getValue('diastolic2.value');
        bpFlag(diastolic,systolic,'second_reading_bp');
    }

function removeFlags(display){

            jq('#' + display).last().removeClass("green_flag");
            jq('#' + display).last().removeClass("red_flag");
            jq('#' + display).last().removeClass("yellow_flag");

}
    function bpFlag(diastolic,systolic,display){
            var isNormal = false;
            var isNormalHigh = false;
            var isMild = false;
            var isModerate = false;
            var isSevere = false;
            var cBp = bpCategory();
            var isDiabetic = getValue('diabetes_status.value'); 

            isNormal = bPStatus(diastolic,systolic,
                cBp.normal.min.systolic,cBp.normal.max.systolic,
                cBp.normal.min.diastolic,cBp.normal.max.diastolic
            );

            if(isNormal){            
            if(isDiabetic == 165088){
                jq('#'+display).last().addClass("yellow_flag");             

            }else{
               jq('#' + display ).last().addClass("green_flag");
            }
            return;
            }

            isNormalHigh = bPStatus(diastolic,systolic,
                cBp.high_normal.min.systolic,cBp.high_normal.max.systolic,
                cBp.high_normal.min.diastolic,cBp.high_normal.max.diastolic
            );

            if(isNormalHigh){


            if(isDiabetic == 165088){
                jq('#' + display ).last().addClass("red_flag");
            }else{
               jq('#'+display).last().addClass("green_flag");
            }
            return;
            }

            isMild = bPStatus(diastolic,systolic,
                cBp.mild.min.systolic,cBp.mild.max.systolic,
                cBp.mild.min.diastolic,cBp.mild.max.diastolic
            );

            if(isMild){

            if(isDiabetic == 165088){
                jq('#'+display).last().addClass("red_flag");
            }else{
               jq('#' + display).last().addClass("green_flag");
            }

            return;
            }

            isModerate = bPStatus(diastolic,systolic,
                cBp.moderate.min.systolic,cBp.moderate.max.systolic,
                cBp.moderate.min.diastolic,cBp.moderate.max.diastolic
            );

            if(isModerate){
            if(isDiabetic == 165088){
                jq('#'+display).last().addClass("red_flag");
            }else{
               jq('#'+display).last().addClass("yellow_flag");
            }
            return;
            }

            isSevere = bPStatus(diastolic,systolic,
                cBp.severe.min.systolic,cBp.severe.max.systolic,
                cBp.severe.min.diastolic,cBp.severe.max.diastolic
            );

            if(isSevere){
             jq('#'+display).last().addClass("red_flag");
            return;
            }
    }

function bpCategory(){
    return {
                    normal:{ 
                        min:{diastolic:80,systolic:120},
                        max:{diastolic:84,systolic:129}
                    },
                    high_normal:{ 
                        min:{diastolic:85,systolic:130},
                        max:{diastolic:89,systolic:139}
                    },
                    mild:{ 
                        min:{diastolic:90,systolic:140},
                        max:{diastolic:99,systolic:159}
                    },
                    moderate:{ 
                        min:{diastolic:100,systolic:160},
                        max:{diastolic:109,systolic:179}
                    },
                    severe:{ 
                        min:{diastolic:110,systolic:180},
                        max:{diastolic:1000,systolic:1000}
                    }
            };
}

        function onNHIFStatusChange() {
            var display = '';
            var nhif = getValue('nhif.value');
            jq('#other_nhif :input').prop('disabled', true);            
            if (nhif==1066)  {                
                    display = 'Encourage patient to enroll';
            }            
            if (nhif==1065)  {                
                    jq('#other_nhif :input').prop('disabled', false);  
            }            
            jq('#nhif-comment').html(display);
          }
