htmlform.script:
function onWeightOrHeightChange() {
    var display = '';
    var weightKg = parseFloat(getValue('weight.value'));
    var heightM = parseFloat(getValue('height.value')) / 100;
    if (!isNaN(weightKg) && !isNaN(heightM))  {
        var bmi = weightKg / (heightM * heightM);
        if (isNaN(bmi) || bmi < 10 || bmi > 50) {
            display = 'Abnormal value. Were height and height entered correctly?';
        }
        else {
            display = bmi.toFixed(2);
        }
    }

    jq('#calculated-bmi').html(display);
}

function onNHIFStatusChange() {
    var display = '';
    var nhif = getValue('nhif.value');            
    if (nhif==1066)  {                
            display = 'Encourage patient to enroll';
    }            
    jq('#nhif-comment').html(display);
  }
function patientRefered(){
    var val= getValue('referal.value');
    jq('#refering_facility :input').prop('disabled', true);
    if(val == 1065){
        jq('#refering_facility :input').prop('disabled', false);
     }
}
function patientReferedOther(){
    var val= getValue('referra-options.value');
    jq('#refering_other :input').prop('disabled', true);
    if(val == 522){
        jq('#refering_other :input').prop('disabled', false);
     }
}
